'use strict';var walletdetection={applepay:async function(){return!!(window.ApplePaySession&&window.ApplePaySession.canMakePayments());},googlepay:async function(){return $.ajax({url:'https://pay.google.com/gp/p/js/pay.js',dataType:'script',}).then(function(){const paymentsClient=new google.payments.api.PaymentsClient({environment:'TEST'});return paymentsClient.isReadyToPay({apiVersion:2,apiVersionMinor:0,allowedPaymentMethods:[{type:'CARD',parameters:{allowedAuthMethods:["PAN_ONLY","CRYPTOGRAM_3DS"],allowedCardNetworks:["AMEX","DISCOVER","INTERAC","JCB","MASTERCARD","VISA"]}}],})}).then(function(response){return!!response.result;});},name_map:{applepay:gettext('Apple Pay'),googlepay:gettext('Google Pay'),}}
$(function(){const wallets=$('[data-wallets]').map(function(index,pm){return pm.getAttribute("data-wallets").split("|");}).get().flat().filter(function(item,pos,self){return item&&self.indexOf(item)==pos;});wallets.forEach(function(wallet){const labels=$('[data-wallets*='+wallet+'] + .accordion-label-text').append('<span class="wallet wallet-loading" data-wallet="'+wallet+'"> <span aria-hidden="true" class="fa fa-cog fa-spin"></span></span>')
walletdetection[wallet]().then(function(result){const spans=labels.find(".wallet-loading[data-wallet="+wallet+"]");if(result){spans.removeClass('wallet-loading').hide().text(', '+walletdetection.name_map[wallet]).fadeIn(300);}else{spans.remove();}}).catch(function(result){labels.find(".wallet-loading[data-wallet="+wallet+"]").remove();})});});;